			break;
		case 3:
			AppendCommentBuffer(yytext, strlen(yytext));
			break;
		case 4:
			AppendCommentBuffer(yytext, strlen(yytext));
			break;
		case 5:
			AppendCommentBuffer(yytext, strlen(yytext));
			BEGIN(STATE_INITIAL);
			break;
		case 6:
			AppendCommentBuffer(yytext, strlen(yytext));
			break;
		case 7:
			AppendCommentBuffer(yytext, strlen(yytext));
			break;
		case 8:
			BEGIN(STATE_STR);
			string_buf_ptr = Value;
			string_buf_end = Value + (sizeof(Value) / sizeof(char) - 1);
			break;
		case 9:
			BEGIN(STATE_INITIAL);
			*string_buf_ptr = '\0';
