			break;
		case 50:
			return S_DOTDOT;
			break;
		case 51:
			return S_BACKQUOTE;
			break;
		case 52:
			return S_PLUS;
			break;
		case 53:
			return S_MINUS;
			break;
		case 54:
			return S_MULT;
			break;
		case 55:
			return S_DIV;
			break;
		case 56:
			return S_POW;
			break;
		case 57:
			return S_COMMA;
			break;
		case 58:
			return S_DOT;
			break;
		case 59:
			return S_LBRACK;
			break;
		case 60:
			return S_RBRACK;
			break;
		case 61:
			return S_LPAREN;
			break;
		case 62:
			return S_RPAREN;
			break;
		case 63:
			return S_LBRACE;
			break;
		case 64:
			return S_RBRACE;
			break;
		case 65:
			Value[0] = '~'; Value[1] = '\0';
			return S_IDENT;
			break;
		case 66:
			Value[0] = yytext[1];
			Value[1] = '\0';
			return S_CHAR;
			break;
		case 67:
			Value[0] = decode_backslashed(yytext[2]);
			Value[1] = '\0';
			return S_CHAR;
			break;
		case 68:
			Value[0] = yytext[1];
			Value[1] = '\0';
			SyntaxError("misplaced single-quote in character constant");
			return S_CHAR;
			break;
		case 69:
			fill_Value(yytext, strlen(yytext));
			return S_INT;
			break;
		case 70:
			fill_Value(yytext, strlen(yytext));
			return S_DOUBLE;
			break;
		case 71:
			fill_Value(yytext, strlen(yytext));
			return S_IDENT;
			break;
		case 72:
			*In = '\0';
			return S_EOF;
			break;
		case YY_STATE_EOF(STATE_INITIAL):
		case YY_STATE_EOF(STATE_COMMENT):
		case YY_STATE_EOF(STATE_STR):
			*In = '\0';
